import React from "react";
import styled from "styled-components";
import RightAlignedTimeline from "./RightAlignedTimeline";
import Arrow from "./Arrow";

const ButtonBackG = styled.div`
background-color: #dacff3;
padding: 20px;
`

class OpenBackground extends React.Component {

    state = { showing: false };

    render() {
        const { showing } = this.state;
        return (
            <div>
                <div className="bg-grey" onClick={() => this.setState({ showing: !showing })}>{ showing 
                    ? "Close"
                    :  "Open"
                }  </div>
                { showing 
                    ? <RightAlignedTimeline />
                    : null
                }
            </div>  
        )
    }
}

export default OpenBackground
