import React from "react";
import RightAlignedTimeline from "./RightAlignedTimeline";
import { Button } from "@material-ui/core";
import styled from "styled-components";
import { Typography } from "@material-ui/core";

const ContainerBg = styled.div`
padding: 3% 2% 0 2%;
	display: flex;
	flex-direction: row;
	flex-wrap: nowrap;
	justify-content: space-between;
	align-items: center;
	align-content: space-between;

`

const ContainerSentence = styled.div`
width: 70%;
`

class OpenBackground extends React.Component {

    state = { showing: false };

    render() {
        const { showing } = this.state;
        return (
            <div>
          <Typography variant="h2">Hey hey, Lorem ipsum</Typography><br></br>
          <ContainerBg align="left">
              <ContainerSentence>
          <Typography  variant="h3" >
            Dolor sit amet consectetur adipisicing elit. Facere cum amet iste,
            commodi reprehenderit similique nesciunt quidem reiciendis debitis
            placeat, maiores quisquam eius. Dolore tenetur obcaecati eligendi
            dolorum blanditiis culpa!
          </Typography>
          </ContainerSentence>
                <Button variant="contained" color="secondary" onClick={() => this.setState({ showing: !showing })}>{ showing 
                    ? "Close"
                    :  "Open"
                }  </Button></ContainerBg>
                { showing 
                    ? <RightAlignedTimeline />
                    : null
                }
            </div>  
        )
    }
}

export default OpenBackground
