import React from "react";
import styled from "styled-components";
import RightAlignedTimeline from "./RightAlignedTimeline";

const ButtonBackG = styled.div`
background-color: blue;
padding: 20px;
`

const ShowChrono = styled.div`
-webkit-transition: all 0.5s 0s ease-in-out;
-moz-transition: all 0.5s 0s ease-in-out;
-o-transition: all 0.5s 0s ease-in-out;
transition: all 0.5s 0s ease-in-out;
`

class OpenBackground extends React.Component {

    state = { showing: false };

    render() {
        const { showing } = this.state;
        return (
            <div>
                <ButtonBackG onClick={() => this.setState({ showing: !showing })}>toggle</ButtonBackG>
                { showing 
                    ? <ShowChrono><div><RightAlignedTimeline /></div></ShowChrono>
                    : null
                }
            </div>  
        )
    }
}

export default OpenBackground
