import React from "react";
import styled from "styled-components";
import RightAlignedTimeline from "./RightAlignedTimeline";

const ButtonBackG = styled.div`
background-color: blue;
padding: 20px;
`

class OpenBackground extends React.Component {

    state = { showing: false };

    render() {
        const { showing } = this.state;
        return (
            <div>
                { showing 
                    ? <RightAlignedTimeline />
                    : null
                }
                <ButtonBackG onClick={() => this.setState({ showing: !showing })}>{ showing 
                    ? "Close"
                    : "Open"
                }</ButtonBackG>

            </div>  
        )
    }
}

export default OpenBackground
