import { createTheme } from "@material-ui/core/styles";

const theme = createTheme({
  palette: {
    primary: {
      main: "#F58A53",
      contrastText: "#FFFFFF",
    },
    secondary: {
      main: "#b1a9fc",
      contrastText: "#FFFFFF",
    },
    darkPurple: {
      main: '#9f97e5',
    },
  },
  typography: {
    body2: {
      fontSize: "1em",
    },
    body1: {
      fontWeight: 300,
      fontSize: "1em",
    },
    h1: {
      fontWeight: 500,
      fontSize: "3em",
      fontFamily: "DM Serif Display",
    },
    h2: {
      fontWeight: 500,
      fontSize: "2em",
      fontFamily: "DM Serif Display",
    },
    h3: {
      fontWeight: 500,
      fontSize: "1.4em",
      fontFamily: "DM Serif Display",
    },
    h4: {
      fontWeight: 500,
      fontSize: "1.2em",
      fontFamily: "DM Serif Display",
    },
    h6: {
      fontWeight: 500,
      fontSize: "1.1em",
      fontStyle: "italic"
    },
  },
});

export default theme;
