import { Button, Typography } from '@material-ui/core';
import React, { useState } from 'react';

function Skills(props) {
    const [isShown, setIsShown] = useState(false);

    return (
      <div className="component-skills">
        <div 
          onMouseEnter={() => setIsShown(true)}
          onMouseLeave={() => setIsShown(false)}>

        {isShown && (
          <div>
              <h3>{props.nom}</h3>
<label for="file">{props.label1}</label>
<progress id="file" max="100" value={props.value1}>{props.progress1}</progress>
<br/>
<label for="file">{props.label2}</label>
<progress id="file" max="100" value={props.value2}>{props.progress2}</progress>
<br/>
<label for="file">{props.label3}</label>
<progress id="file" max="100" value={props.value3}>{props.progress3}</progress>
<br/>
          </div>

        )}
         {!isShown && (
          <div className="yoy">
<h3>{props.nom}</h3>
<button>Hover me!</button>
         </div>
        )}


        </div> 
      </div>
    );
  }

export default Skills
