import {createTheme} from '@material-ui/core/styles'

const theme = createTheme({
    palette: {
        primary: {
            main: '#F58A53',
            contrastText: '#FFFFFF'
        },
        secondary: {
            main: '#BEA5E0', 
            contrastText: '#FFFFFF'
        }
    },
    typography: {
        body2: {
          fontSize: '1em',
        },
        body1: {
          fontWeight: 300,
          fontSize: '1em',
        },
        h2: {
            fontWeight: 300,
            fontSize: '1em',
            fontFamily: 'Roboto Slab',
          }
    },
})

export default theme;
