import React from "react";
import RightAlignedTimeline from "./RightAlignedTimeline";
import Arrow from "./Arrow";
import { Button } from "@material-ui/core";
import theme from './theme'

const useStyles = makeStyles((theme) => ({
    paper: {
      padding: '6px 16px',
    },
    secondaryTail: {
      backgroundColor: theme.palette.secondary.main,
    },
  }));

class OpenBackground extends React.Component {

    state = { showing: false };

    render() {
        const { showing } = this.state;
        return (
            <div>
                <Button variant="contained" color="secondary" onClick={() => this.setState({ showing: !showing })}>{ showing 
                    ? "Close"
                    :  "Open"
                }  </Button>
                { showing 
                    ? <RightAlignedTimeline />
                    : null
                }
            </div>  
        )
    }
}

export default OpenBackground
