import React from 'react'
import styled from "styled-components";


const DivRight = styled.div`
transform: rotate(-90deg);
padding: 10%;
`

const ArrowSpan = styled.span`
display: block;
    width: 30px;
    height: 30px;
    border-bottom: 5px solid #ec5136;
    border-right: 5px solid #ec5136;
    margin: -10px;
    animation: animate 3s infinite;
`

const DivArrow = styled.div`

&span:nth-child(2){
    animation-delay: -0.2s;
}
span:nth-child(3){
    animation-delay: -0.4s;
}

@keyframes animate {
    0%{
        opacity: 0;
        transform: rotate(45deg) translate(-20px,-20px);
    }
    50%{
        opacity: 1;
    }
    100%{
        opacity: 0;
        transform: rotate(45deg) translate(20px,20px);
    }
}
`

function Arrow() {
    return (
        <DivRight>
<DivArrow>
                <ArrowSpan></ArrowSpan>
                <ArrowSpan></ArrowSpan>
                <ArrowSpan></ArrowSpan>

</DivArrow>
</DivRight>
    )
}

export default Arrow
